attributes.doc:
apiVersion: apps/v1
kind: Deployment
metadata:
  name: {{ service_name }}
  namespace: {{ namespace }}
spec:
  replicas: {{ replicas }}

apiVersion: v1
kind: Service
metadata:
  name: {{ service_name }}
  namespace: {{ namespace }}
spec:
  ports:
  - port: {{ port }}

apiVersion: autoscaling/v2
kind: HorizontalPodAutoscaler
metadata:
  name: {{ service_name }}
spec:
  minReplicas: {{ min_replicas }}
  maxReplicas: {{ max_replicas }}

service:
  name: {{ service_name }}
  port: {{ port }}
  environment: {{ environment }}

database:
  host: {{ db_host }}
  port: {{ db_port }}
  name: {{ db_name }}
  pool_size: {{ pool_size }}

cache:
  host: {{ redis_host }}
  port: {{ redis_port }}
  cluster_mode: {{ cluster_mode }}

logging:
  level: {{ log_level }}
  format: {{ log_format }}
  output: {{ log_output }}

tracing:
  enabled: {{ tracing_enabled }}
  sampler: {{ sampler_type }}
  endpoint: {{ collector_endpoint }}

replicaCount: {{ replicas }}
image:
  repository: {{ image_repository }}
  tag: {{ image_tag }}
resources:
  limits:
    cpu: {{ cpu_limit }}
    memory: {{ memory_limit }}

SERVICE_NAME={{ service_name }}
ENVIRONMENT={{ environment }}
LOG_LEVEL={{ log_level }}
PORT={{ port }}
